import React, { useEffect, useState } from "react";
import Papa from "papaparse";

export default function NatureCulturePage() {
  const [places, setPlaces] = useState([]);
  const [selectedPlace, setSelectedPlace] = useState(null);

  useEffect(() => {
    Papa.parse("/spots.csv", {
      download: true,
      header: true,
      complete: (res) => {
        const rows = res.data.filter((r) => r.name && r.image);
        setPlaces(rows);
      },
    });
  }, []);

  return (
    <div className="p-6">
      {selectedPlace ? (
        // Details View
        <div className="max-w-2xl mx-auto bg-white shadow-lg rounded-2xl p-6">
          <img
            src={selectedPlace.image}
            alt={selectedPlace.name}
            className="h-64 w-full object-cover rounded-lg mb-4"
          />
          <h2 className="text-2xl font-bold mb-2">{selectedPlace.name}</h2>
          <p className="text-gray-700 mb-4">{selectedPlace.description}</p>
          <button
            onClick={() => setSelectedPlace(null)}
            className="px-4 py-2 bg-blue-600 text-white rounded-lg hover:bg-blue-700"
          >
            Back
          </button>
        </div>
      ) : (
        // Grid View
        <div className="grid grid-cols-1 sm:grid-cols-2 md:grid-cols-3 gap-6">
          {places.map((p, i) => (
            <div
              key={i}
              onClick={() => setSelectedPlace(p)}
              className="cursor-pointer rounded-2xl shadow-md overflow-hidden border bg-white hover:shadow-lg transition"
            >
              <img
                src={p.image}
                alt={p.name}
                className="h-48 w-full object-cover"
              />
              <div className="p-3">
                <h3 className="text-lg font-bold">{p.name}</h3>
              </div>
            </div>
          ))}
        </div>
      )}
    </div>
  );
}
