import React, { useEffect, useState } from "react";

export default function ProfilePage() {
  const storedUser = JSON.parse(localStorage.getItem("user"));
  const token = localStorage.getItem("token");

  const [user, setUser] = useState(null);
  const [preferences, setPreferences] = useState([]);
  const [itineraries, setItineraries] = useState([]);
  const [editing, setEditing] = useState(false);
  const [loading, setLoading] = useState(true);

  const preferenceOptions = ["Nature", "Culture", "Food", "History", "Adventure"];

  useEffect(() => {
    const loadUserData = async () => {
      if (!storedUser?.email || !token) {
        setLoading(false);
        return;
      }

      try {
        const res = await fetch(
          `http://localhost:5000/api/users/email/${encodeURIComponent(storedUser.email)}`,
          { headers: { Authorization: `Bearer ${token}` } }
        );
        if (!res.ok) throw new Error("Failed to fetch user profile");
        const data = await res.json();
        setUser(data);
        setPreferences(Array.isArray(data.preferences) ? data.preferences : []);
      } catch (err) {
        console.error("Error fetching user:", err);
      } finally {
        setLoading(false);
      }
    };
    loadUserData();
  }, [storedUser?.email, token]);

  useEffect(() => {
    const loadItineraries = async () => {
      if (!storedUser?.id || !token) return;

      try {
        const res = await fetch(`http://localhost:5000/api/itineraries`, {
          headers: { Authorization: `Bearer ${token}` }
        });
        if (!res.ok) throw new Error("Failed to fetch itineraries");
        const data = await res.json();
        if (Array.isArray(data.itineraries)) setItineraries(data.itineraries);
      } catch (err) {
        console.error("Error fetching itineraries:", err);
      }
    };
    loadItineraries();
  }, [storedUser?.id, token]);

  const handleTogglePreference = (option) => {
    setPreferences(prev =>
      prev.includes(option) ? prev.filter(p => p !== option) : [...prev, option]
    );
  };

  const handleSave = async () => {
    if (!user?.email || !token) return;

    try {
      const res = await fetch(
        `http://localhost:5000/api/users/email/${encodeURIComponent(user.email)}/preferences`,
        {
          method: "PUT",
          headers: {
            "Content-Type": "application/json",
            Authorization: `Bearer ${token}`,
          },
          body: JSON.stringify({ preferences }),
        }
      );
      const data = await res.json();
      if (!res.ok) throw new Error(data.message || "Failed to save preferences");
      setUser({ ...user, preferences });
      setEditing(false);
    } catch (err) {
      console.error("Error saving preferences:", err);
    }
  };

  if (loading) return <div style={{ padding: 24, fontSize: "1.2rem", textAlign: "center" }}>Loading your profile...</div>;
  if (!user) return <div style={{ padding: 24, fontSize: "1.2rem", textAlign: "center" }}>No user data found.</div>;

  return (
    <div style={{ minHeight: "100vh", background: "linear-gradient(135deg, #c8e6c9, #bbdefb)", padding: "40px 20px", fontFamily: "Segoe UI, sans-serif" }}>
      <div style={{ maxWidth: "900px", margin: "0 auto", background: "rgba(255,255,255,0.85)", backdropFilter: "blur(10px)", borderRadius: 20, padding: "30px 40px", boxShadow: "0 8px 25px rgba(0,0,0,0.15)" }}>
        <h1 style={{ fontSize: "2rem", fontWeight: 700, textAlign: "center", color: "#0d47a1", marginBottom: 30 }}>User Profile</h1>
        <div style={{ borderRadius: 16, padding: 24, background: "rgba(255,255,255,0.95)", boxShadow: "0 4px 14px rgba(0,0,0,0.1)", marginBottom: 28 }}>
          <p><strong>Username:</strong> {user.username}</p>
          <p><strong>Email:</strong> {user.email}</p>
          <p><strong>Full Name:</strong> {`${user.first_name || ""} ${user.middle_name || ""} ${user.last_name || ""}`}</p>
          <p><strong>Age:</strong> {user.age || "Not provided"}</p>

          <div style={{ marginTop: 16 }}>
            <strong>Preferences:</strong>
            {editing ? (
              <div>
                {preferenceOptions.map(option => (
                  <label key={option} style={{ display: "block", marginTop: 8, cursor: "pointer" }}>
                    <input
                      type="checkbox"
                      value={option}
                      checked={preferences.includes(option)}
                      onChange={() => handleTogglePreference(option)}
                      style={{ marginRight: 6 }}
                    />
                    {option}
                  </label>
                ))}
                <div style={{ marginTop: 16 }}>
                  <button onClick={handleSave} style={{ background: "#1976d2", color: "#fff", padding: "10px 20px", border: "none", borderRadius: 8, cursor: "pointer", marginRight: 10 }}>Save</button>
                  <button onClick={() => { setPreferences(user.preferences || []); setEditing(false); }} style={{ background: "#bdbdbd", color: "#fff", padding: "10px 20px", border: "none", borderRadius: 8, cursor: "pointer" }}>Cancel</button>
                </div>
              </div>
            ) : (
              <ul style={{ marginTop: 8, paddingLeft: 20 }}>
                {preferences.length > 0 ? preferences.map((p,i) => <li key={i} style={{ color: "#2e7d32", fontWeight: 500 }}>{p}</li>) : <li>No preferences set.</li>}
              </ul>
            )}
          </div>
          {!editing && <button onClick={() => setEditing(true)} style={{ marginTop: 16, background: "#43a047", color: "#fff", padding: "10px 22px", border: "none", borderRadius: 8, cursor: "pointer" }}>Edit Preferences</button>}
        </div>

        <div style={{ borderRadius: 16, padding: 24, background: "rgba(255,255,255,0.95)", boxShadow: "0 4px 14px rgba(0,0,0,0.1)" }}>
          <h2 style={{ fontSize: "1.5rem", marginBottom: 16, color: "#0d47a1" }}>Saved Itineraries</h2>
          {itineraries.length > 0 ? (
            <ul style={{ listStyle: "none", padding: 0 }}>
              {itineraries.map(itinerary => (
                <li key={itinerary.id} style={{ background: "rgba(227,242,253,0.9)", borderRadius: 12, padding: "14px 18px", marginBottom: 10 }}>
                  <p style={{ margin: 0 }}>
                    <strong style={{ color: "#0d47a1" }}>{itinerary.name}</strong>
                    <br />
                    <span>Status: {itinerary.status || "N/A"}</span>
                    <br />
                    <small style={{ color: "#616161" }}>Saved on: {itinerary.created_at ? new Date(itinerary.created_at).toLocaleString() : "Unknown date"}</small>
                  </p>
                </li>
              ))}
            </ul>
          ) : (
            <p style={{ color: "#555" }}>No itineraries found.</p>
          )}
        </div>
      </div>
    </div>
  );
}
