import React, { useEffect, useState } from "react";
import { MapContainer, TileLayer, Marker, Popup, CircleMarker } from "react-leaflet";
import "leaflet/dist/leaflet.css";

export default function AdminDashboard() {
  const [pois, setPois] = useState([]);
  const [usersCount, setUsersCount] = useState(0);
  const [editingPoi, setEditingPoi] = useState(null);

  const user = JSON.parse(localStorage.getItem("user"));
  const isAdmin = user?.role === "admin";

  useEffect(() => {
    if (isAdmin) {
      fetch("http://localhost:5000/api/pois")
        .then((res) => res.json())
        .then((data) => setPois(data))
        .catch((err) => console.error("Failed to fetch POIs", err));

      fetch("http://localhost:5000/api/users/count")
        .then((res) => res.json())
        .then((data) => setUsersCount(data.count))
        .catch((err) => console.error("Failed to fetch users count", err));
    }
  }, [isAdmin]);

  const saveEdit = (updatedPoi) => {
    fetch("http://localhost:5000/api/pois", {
      method: "POST",
      headers: { "Content-Type": "application/json" },
      body: JSON.stringify(updatedPoi),
    })
      .then((res) => res.json())
      .then(() => {
        setPois(pois.map((p) => (p.id === updatedPoi.id ? updatedPoi : p)));
        setEditingPoi(null);
      })
      .catch((err) => console.error("Failed to save POI", err));
  };

  if (!isAdmin) return <h2>Access Denied</h2>;

  const mostVisited = pois.reduce(
    (prev, current) =>
      !prev || (current.visitorsCount || 0) > (prev.visitorsCount || 0) ? current : prev,
    null
  );

  return (
    <div style={{ display: "flex" }}>
      <div
        style={{
          width: "220px",
          background: "#2563eb",
          color: "#fff",
          height: "100vh",
          padding: "20px",
          display: "flex",
          flexDirection: "column",
        }}
      >
        <h2>Admin Panel</h2>
        <p>Total Users: {usersCount}</p>
        <p>Total POIs: {pois.length}</p>
        {mostVisited && <p>Most Visited: {mostVisited.name}</p>}
      </div>

      <div style={{ flexGrow: 1, padding: "20px" }}>
        <h1>Admin Dashboard</h1>
        <MapContainer
          center={[14.1467, 121.4708]}
          zoom={13}
          style={{ height: "500px", width: "100%" }}
        >
          <TileLayer
            url="https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png"
            attribution='&copy; OpenStreetMap contributors'
          />
          {pois.map((poi) =>
            poi.id === mostVisited?.id ? (
              <CircleMarker
                key={poi.id}
                center={[poi.lat, poi.lng]}
                radius={15}
                color="red"
              >
                <Popup>
                  <strong>{poi.name} (Most Visited)</strong>
                  <br />
                  {poi.description}
                  <br />
                  Visitors: {poi.visitors}
                </Popup>
              </CircleMarker>
            ) : (
              <Marker key={poi.id} position={[poi.lat, poi.lng]}>
                <Popup>
                  <strong>{poi.name}</strong>
                  <br />
                  {poi.description}
                  <br />
                  Visitors: {poi.visitors}
                  <br />
                  Tags: {poi.tags}
                  <br />
                  {poi.image && (
                    <img
                      src={poi.image}
                      alt={poi.name}
                      style={{ width: "150px", borderRadius: "8px", marginTop: "4px" }}
                    />
                  )}
                  <br />
                  <button onClick={() => setEditingPoi(poi)}>Edit</button>
                </Popup>
              </Marker>
            )
          )}
        </MapContainer>

        {editingPoi && (
          <div
            style={{
              position: "fixed",
              top: 50,
              left: "50%",
              transform: "translateX(-50%)",
              background: "white",
              padding: 20,
              border: "1px solid #ccc",
              zIndex: 1000,
              maxWidth: 400,
            }}
          >
            <h3>Edit POI</h3>
            <label>Name:</label>
            <input
              type="text"
              value={editingPoi.name}
              onChange={(e) => setEditingPoi({ ...editingPoi, name: e.target.value })}
            />
            <br />
            <label>Description:</label>
            <textarea
              value={editingPoi.description}
              onChange={(e) =>
                setEditingPoi({ ...editingPoi, description: e.target.value })
              }
            />
            <br />
            <label>Visitors:</label>
            <select
              value={editingPoi.visitors}
              onChange={(e) =>
                setEditingPoi({ ...editingPoi, visitors: e.target.value })
              }
            >
              <option>Very High</option>
              <option>High</option>
              <option>Moderate</option>
              <option>Low</option>
            </select>
            <br />
            <label>Tags (comma-separated):</label>
            <input
              type="text"
              value={editingPoi.tags}
              onChange={(e) => setEditingPoi({ ...editingPoi, tags: e.target.value })}
            />
            <br />
            <label>Latitude:</label>
            <input
              type="number"
              value={editingPoi.lat}
              onChange={(e) =>
                setEditingPoi({ ...editingPoi, lat: parseFloat(e.target.value) })
              }
            />
            <br />
            <label>Longitude:</label>
            <input
              type="number"
              value={editingPoi.lng}
              onChange={(e) =>
                setEditingPoi({ ...editingPoi, lng: parseFloat(e.target.value) })
              }
            />
            <br />
            <label>Image URL:</label>
            <input
              type="text"
              value={editingPoi.image}
              onChange={(e) => setEditingPoi({ ...editingPoi, image: e.target.value })}
            />
            <br />
            <button onClick={() => saveEdit(editingPoi)}>Save</button>
            <button onClick={() => setEditingPoi(null)} style={{ marginLeft: 8 }}>
              Cancel
            </button>
          </div>
        )}
      </div>
    </div>
  );
}
