import React, { useEffect, useMemo, useState } from "react";
import { MapContainer, TileLayer, Marker, Popup } from "react-leaflet";
import "leaflet/dist/leaflet.css";
import Papa from "papaparse";

const CENTER = { lat: 14.1467, lng: 121.4708 };
const IMAGE = "/images/taytayfalls.jpg"; // single image in public/images

export default function Home() {
  const [spots, setSpots] = useState([]);

  useEffect(() => {
    fetch("/data/spots.csv")       // <-- fetch from public folder
      .then((res) => res.text())
      .then((csvText) => {
        Papa.parse(csvText, {
          header: true,
          skipEmptyLines: true,
          complete: (res) => {
            const rows = res.data
              .filter((r) => r.name && r.lat && r.lng)
              .map((r) => ({
                name: r.name,
                lat: parseFloat(r.lat),
                lng: parseFloat(r.lng),
                visitors: r.visitors || "Moderate",
                image: IMAGE,
              }));
            setSpots(rows);
          },
        });
      });
  }, []);

  const topSpots = useMemo(() => {
    const order = { "Very High": 3, High: 2, Moderate: 1, Low: 0 };
    return [...spots]
      .sort((a, b) => (order[b.visitors] || 0) - (order[a.visitors] || 0))
      .slice(0, 5);
  }, [spots]);

  return (
    <div style={{ display: "grid", gap: 16, gridTemplateColumns: "1fr 1fr" }}>
      <div className="card">
        <h2>Majayjay Overview</h2>
        <div style={{ height: "500px" }}>
          <MapContainer center={CENTER} zoom={13} style={{ height: "100%", width: "100%" }}>
            <TileLayer
              attribution='&copy; <a href="https://www.openstreetmap.org/">OSM contributors</a>'
              url="https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png"
            />
            {topSpots.map((s, i) => (
              <Marker key={i} position={[s.lat, s.lng]}>
                <Popup>
                  <strong>{s.name}</strong>
                  <br />
                  Visitors: {s.visitors}
                  <br />
                  <img src={s.image} alt={s.name} style={{ width: "150px", borderRadius: "8px", marginTop: "4px" }} />
                </Popup>
              </Marker>
            ))}
          </MapContainer>
        </div>
      </div>

      <div className="card">
        <h2>Most Visited Places</h2>
        <ul>
          {topSpots.length > 0 ? (
            topSpots.map((s, i) => (
              <li key={i}>
                <strong>{s.name}</strong> â€” {s.visitors} visitors
              </li>
            ))
          ) : (
            <li>No spots found in CSV</li>
          )}
        </ul>
      </div>
    </div>
  );
}
