import React, { useEffect, useState } from "react";
import { MapContainer, TileLayer, Marker } from "react-leaflet";
import "leaflet/dist/leaflet.css";
import Papa from "papaparse";

const CENTER = { lat: 14.1467, lng: 121.4708 };

export default function MapPage() {
  const [spots, setSpots] = useState([]);
  const [selected, setSelected] = useState(null);

  useEffect(() => {
    fetch("/data/spots.csv")
      .then(res => res.text())
      .then(csvText => {
        Papa.parse(csvText, {
          header: true,
          skipEmptyLines: true,
          complete: (res) => {
            const rows = res.data
              .filter(r => r.name && r.lat && r.lng)
              .map(r => ({
                name: r.name,
                lat: parseFloat(r.lat),
                lng: parseFloat(r.lng),
                description: r.description || "No description available",
                tags: (r.tags || "").split(",").map(t => t.trim()).filter(Boolean),
                visitors: r.visitors || "Moderate",
                image: r.image || null,
              }));
            setSpots(rows);
          },
        });
      });
  }, []);

  return (
    <div className="card" style={{ padding: 0 }}>
      <div style={{ height: selected ? "50vh" : "80vh", transition: "height 0.3s" }}>
        <MapContainer center={CENTER} zoom={13} style={{ height: "100%", width: "100%" }}>
          <TileLayer
            attribution='&copy; <a href="https://www.openstreetmap.org/">OSM contributors</a>'
            url="https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png"
          />
          {spots.map((s, i) => (
            <Marker key={i} position={[s.lat, s.lng]} eventHandlers={{ click: () => setSelected(s) }} />
          ))}
        </MapContainer>
      </div>

      {selected && (
        <div className="spot-description p-4 border-t bg-white shadow-md" style={{ position: "relative" }}>
          <button onClick={() => setSelected(null)} style={{ position: "absolute", top: 10, right: 10, border: "none", background: "red", color: "white", padding: "5px 10px", borderRadius: 5, cursor: "pointer" }}>âœ– Close</button>
          <h2 className="text-lg font-bold">{selected.name}</h2>
          {selected.image && <img src={selected.image} alt={selected.name} style={{ width: "100%", maxHeight: 250, objectFit: "cover", borderRadius: 8, marginBottom: 10 }} />}
          <p>{selected.description}</p>
          {selected.tags?.length > 0 && <p><strong>Tags:</strong> {selected.tags.join(", ")}</p>}
          <p><strong>Visitors:</strong> {selected.visitors}</p>
        </div>
      )}
    </div>
  );
}
