import React, { useEffect, useState } from "react";
import { MapContainer, TileLayer, Marker } from "react-leaflet";
import "leaflet/dist/leaflet.css";
import "../leafletfix"; // fix marker icons
import Papa from "papaparse";

const CENTER = { lat: 14.1467, lng: 121.4708 };

export default function MapPage() {
  const [spots, setSpots] = useState([]);
  const [selected, setSelected] = useState(null);

  useEffect(() => {
    Papa.parse("/spots.csv", {
      download: true,
      header: true,
      complete: (res) => {
        const rows = res.data
          .filter((r) => r.name && r.lat && r.lng)
          .map((r) => ({
            name: r.name,
            lat: parseFloat(r.lat),
            lng: parseFloat(r.lng),
            description: r.description || "No description available.",
            tags: (r.tags || "")
              .split(",")
              .map((t) => t.trim())
              .filter(Boolean),
            visitors: r.visitors || "Moderate",
            image: r.image || null, // ✅ load image column
          }));
        setSpots(rows);
      },
    });
  }, []);

  return (
    <div className="card" style={{ padding: 0 }}>
      <div
        className="map-wrapper"
        style={{
          height: selected ? "50vh" : "80vh", // shrink map when a spot is selected
          transition: "height 0.3s ease",
        }}
      >
        <MapContainer
          center={CENTER}
          zoom={13}
          className="h-full w-full"
          style={{ height: "100%", width: "100%" }}
        >
          <TileLayer
            attribution='&copy; <a href="https://www.openstreetmap.org/">OSM contributors</a>'
            url="https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png"
          />
          {spots.map((s, i) => (
            <Marker
              key={i}
              position={[s.lat, s.lng]}
              eventHandlers={{
                click: () => setSelected(s),
              }}
            />
          ))}
        </MapContainer>
      </div>

      {/* Show description when location is selected */}
      {selected && (
        <div
          className="spot-description p-4 border-t bg-white shadow-md"
          style={{ position: "relative" }}
        >
          <button
            onClick={() => setSelected(null)}
            style={{
              position: "absolute",
              top: "10px",
              right: "10px",
              border: "none",
              background: "red",
              color: "white",
              padding: "5px 10px",
              borderRadius: "5px",
              cursor: "pointer",
            }}
          >
            ✖ Close
          </button>
          <h2 className="text-lg font-bold">{selected.name}</h2>

          {/* ✅ Show image if available */}
          {selected.image && (
            <img
              src={selected.image}
              alt={selected.name}
              style={{
                width: "100%",
                maxHeight: "250px",
                objectFit: "cover",
                borderRadius: "8px",
                marginBottom: "10px",
              }}
            />
          )}

          <p>{selected.description}</p>
          {selected.tags?.length ? (
            <p>
              <strong>Tags:</strong> {selected.tags.join(", ")}
            </p>
          ) : null}
          <p>
            <strong>Visitors:</strong> {selected.visitors}
          </p>
        </div>
      )}
    </div>
  );
}
