import React, { useEffect, useState } from "react";
import { MapContainer, TileLayer, Marker, Popup, Polyline } from "react-leaflet";
import "leaflet/dist/leaflet.css";
import { useNavigate, useLocation } from "react-router-dom";

export default function RouteDetails() {
  const location = useLocation();
  const navigate = useNavigate();
  const routeSpots = location.state?.itinerary || [];
  const [currentIndex, setCurrentIndex] = useState(0);
  const [fullRoute, setFullRoute] = useState([]);
  const [loadingRoute, setLoadingRoute] = useState(true);

  const apiKey = "YOUR_API_KEY"; // Replace with your ORS API key

  // Fetch ORS route between two POIs
  const getRouteFromORS = async (from, to) => {
    const isTrail = ["forest", "mountain", "trail"].some((t) =>
      from.type?.toLowerCase().includes(t)
    );
    const profile = isTrail ? "foot-hiking" : "driving-car";

    const url = `https://api.openrouteservice.org/v2/directions/${profile}?api_key=${apiKey}&start=${from.lng},${from.lat}&end=${to.lng},${to.lat}`;
    try {
      const res = await fetch(url);
      const data = await res.json();
      const coords = data.features[0].geometry.coordinates.map(([lng, lat]) => [lat, lng]);
      return { coords, isTrail };
    } catch (err) {
      console.error("ORS route error:", err);
      return { coords: [[from.lat, from.lng], [to.lat, to.lng]], isTrail };
    }
  };

  // Build the full route
  useEffect(() => {
    const buildRoute = async () => {
      const segments = [];
      for (let i = 0; i < routeSpots.length - 1; i++) {
        const { coords, isTrail } = await getRouteFromORS(routeSpots[i], routeSpots[i + 1]);
        segments.push({ coords, isTrail });
      }
      setFullRoute(segments);
      setLoadingRoute(false);
    };
    if (routeSpots.length) buildRoute();
  }, [routeSpots]);

  const nextPOI = () => {
    if (currentIndex < routeSpots.length - 1) setCurrentIndex(currentIndex + 1);
    else alert("You have reached the end of the route!");
  };

  const finishRoute = () => {
    if (window.confirm("Are you sure you want to finish the route?")) {
      alert("Route completed!");
      navigate("/");
    }
  };

  const cancelRoute = () => {
    if (window.confirm("Are you sure you want to cancel the route? You will lose progress.")) {
      alert("Route cancelled.");
      navigate("/");
    }
  };

  const currentPOI = routeSpots[currentIndex];

  if (!routeSpots.length) return <p>No route available.</p>;

  return (
    <div
      style={{
        display: "grid",
        gridTemplateColumns: "1.2fr 0.8fr",
        gap: 20,
        padding: 20,
        background: "linear-gradient(135deg, #e0f7fa, #f1f8e9)",
        minHeight: "100vh",
        animation: "fadeIn 0.8s ease-in-out"
      }}
    >
      {/* Map Section */}
      <div style={{ height: "600px", borderRadius: 12, overflow: "hidden", boxShadow: "0 6px 18px rgba(0,0,0,0.15)" }}>
        {loadingRoute ? (
          <p style={{ textAlign: "center", marginTop: "40%" }}>Loading route...</p>
        ) : (
          <MapContainer
            center={[currentPOI.lat, currentPOI.lng]}
            zoom={14}
            style={{ height: "100%", width: "100%" }}
          >
            <TileLayer
              attribution='&copy; <a href="https://www.openstreetmap.org/">OSM contributors</a>'
              url="https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png"
            />
            {fullRoute.map((seg, i) => (
              <Polyline
                key={i}
                positions={seg.coords}
                color={seg.isTrail ? "green" : "blue"}
                dashArray={seg.isTrail ? "5,10" : undefined}
              />
            ))}
            {routeSpots.map((spot, i) => (
              <Marker key={i} position={[spot.lat, spot.lng]}>
                <Popup>
                  <strong>{spot.name}</strong>
                  <p>{spot.description}</p>
                  {spot.image && (
                    <img
                      src={spot.image}
                      alt={spot.name}
                      style={{
                        width: "160px",
                        borderRadius: "8px",
                        marginTop: 6,
                        boxShadow: "0 4px 10px rgba(0,0,0,0.2)"
                      }}
                    />
                  )}
                </Popup>
              </Marker>
            ))}
          </MapContainer>
        )}
      </div>

      {/* Details Section */}
      <div
        style={{
          background: "#fff",
          borderRadius: 12,
          padding: 20,
          boxShadow: "0 6px 18px rgba(0,0,0,0.12)",
          animation: "slideUp 0.6s ease-in-out"
        }}
      >
        <h2 style={{ margin: "0 0 12px 0", color: "#0d47a1" }}>Current POI</h2>
        <p>
          <strong>{currentPOI.name}</strong>
        </p>
        <p>{currentPOI.description}</p>
        {currentPOI.image && (
          <img
            src={currentPOI.image}
            alt={currentPOI.name}
            style={{
              width: "100%",
              maxWidth: "280px",
              borderRadius: "10px",
              boxShadow: "0 6px 14px rgba(0,0,0,0.2)",
              transition: "transform 0.3s ease",
            }}
            onMouseOver={(e) => (e.currentTarget.style.transform = "scale(1.05)")}
            onMouseOut={(e) => (e.currentTarget.style.transform = "scale(1)")}
          />
        )}

        <p style={{ marginTop: 20, fontStyle: "italic" }}>
          POI {currentIndex + 1} of {routeSpots.length}
        </p>
      </div>

      {/* Sticky Button Bar */}
      <div
        style={{
          position: "fixed",
          bottom: 0,
          left: 0,
          right: 0,
          background: "linear-gradient(90deg, #2196f3, #42a5f5)",
          padding: "12px 20px",
          display: "flex",
          justifyContent: "center",
          gap: "16px",
          zIndex: 1000,
          boxShadow: "0 -3px 12px rgba(0,0,0,0.15)",
        }}
      >
        {currentIndex < routeSpots.length - 1 && (
          <button
            onClick={nextPOI}
            style={{
              padding: "10px 20px",
              border: "none",
              borderRadius: "8px",
              background: "#4caf50",
              color: "#fff",
              fontWeight: "600",
              cursor: "pointer",
              transition: "transform 0.2s ease, background 0.2s ease"
            }}
            onMouseOver={(e) => (e.currentTarget.style.transform = "scale(1.05)")}
            onMouseOut={(e) => (e.currentTarget.style.transform = "scale(1)")}
          >
            Next POI
          </button>
        )}
        <button
          onClick={cancelRoute}
          style={{
            padding: "10px 20px",
            border: "none",
            borderRadius: "8px",
            background: "#f44336",
            color: "#fff",
            fontWeight: "600",
            cursor: "pointer",
            transition: "transform 0.2s ease, background 0.2s ease"
          }}
          onMouseOver={(e) => (e.currentTarget.style.transform = "scale(1.05)")}
          onMouseOut={(e) => (e.currentTarget.style.transform = "scale(1)")}
        >
          Cancel Route
        </button>
        {currentIndex === routeSpots.length - 1 && (
          <button
            onClick={finishRoute}
            style={{
              padding: "10px 20px",
              border: "none",
              borderRadius: "8px",
              background: "#ff9800",
              color: "#fff",
              fontWeight: "600",
              cursor: "pointer",
              transition: "transform 0.2s ease, background 0.2s ease"
            }}
            onMouseOver={(e) => (e.currentTarget.style.transform = "scale(1.05)")}
            onMouseOut={(e) => (e.currentTarget.style.transform = "scale(1)")}
          >
            Finish Route
          </button>
        )}
      </div>

      {/* Keyframes */}
      <style>
        {`
          @keyframes fadeIn {
            from { opacity: 0; }
            to { opacity: 1; }
          }
          @keyframes slideUp {
            from { transform: translateY(20px); opacity: 0; }
            to { transform: translateY(0); opacity: 1; }
          }
        `}
      </style>
    </div>
  );
}
