import React, { useEffect, useState } from "react";

export default function AdminPOIs() {
  const [pois, setPois] = useState([]);
  const [editingPoi, setEditingPoi] = useState(null);

  useEffect(() => {
    fetch("http://localhost:5000/api/pois")
      .then(res => res.json())
      .then(data => setPois(data))
      .catch(err => console.error(err));
  }, []);

  const savePoi = () => {
    fetch(`http://localhost:5000/api/pois/${editingPoi.id}`, {
      method: "PUT",
      headers: { "Content-Type": "application/json" },
      body: JSON.stringify(editingPoi),
    })
      .then(res => res.json())
      .then(() => {
        setPois(pois.map(p => p.id === editingPoi.id ? editingPoi : p));
        setEditingPoi(null);
      })
      .catch(err => console.error(err));
  };

  const deletePoi = (id) => {
    if (!window.confirm("Delete this POI?")) return;
    fetch(`http://localhost:5000/api/pois/${id}`, { method: "DELETE" })
      .then(() => setPois(pois.filter(p => p.id !== id)))
      .catch(err => console.error(err));
  };

  return (
    <div>
      <h2>Points of Interest</h2>
      <ul>
        {pois.map(p => (
          <li key={p.id}>
            {p.name} ({p.tags})
            <button onClick={() => setEditingPoi(p)} style={{ marginLeft: 8 }}>Edit</button>
            <button onClick={() => deletePoi(p.id)} style={{ marginLeft: 8, color: "red" }}>Delete</button>
          </li>
        ))}
      </ul>

      {editingPoi && (
        <div style={{ marginTop: 20, border: "1px solid #ccc", padding: 10 }}>
          <h3>Edit {editingPoi.name}</h3>
          <input type="text" value={editingPoi.name} onChange={e => setEditingPoi({ ...editingPoi, name: e.target.value })} />
          <input type="text" value={editingPoi.tags} onChange={e => setEditingPoi({ ...editingPoi, tags: e.target.value })} />
          <textarea value={editingPoi.description} onChange={e => setEditingPoi({ ...editingPoi, description: e.target.value })} />
          <input type="text" value={editingPoi.image} onChange={e => setEditingPoi({ ...editingPoi, image: e.target.value })} />
          <button onClick={savePoi}>Save</button>
          <button onClick={() => setEditingPoi(null)} style={{ marginLeft: 8 }}>Cancel</button>
        </div>
      )}
    </div>
  );
}
