import React, { useEffect, useState, useRef } from "react";
import { MapContainer, TileLayer, Marker, Popup } from "react-leaflet";
import "leaflet/dist/leaflet.css";
import { useNavigate } from "react-router-dom";
import RoutePlanner from "../components/RoutePlanner";
import WeatherWidget from "../components/WeatherWidget";

const CENTER = { lat: 14.1467, lng: 121.4708 };

export default function Home() {
  const [spots, setSpots] = useState([]);
  const [route, setRoute] = useState([]);
  const [weather, setWeather] = useState("Sunny");
  const [currentTime, setCurrentTime] = useState(new Date());
  const [pendingItinerary, setPendingItinerary] = useState(null);
  const mapRef = useRef(null);
  const markerRefs = useRef({});
  const navigate = useNavigate();

  // Fetch POIs
  useEffect(() => {
    fetch("http://localhost:5000/api/pois")
      .then((res) => res.json())
      .then((data) => {
        const rows = data
          .filter((r) => r.name && r.lat && r.lng)
          .map((r) => ({
            name: r.name,
            lat: parseFloat(r.lat),
            lng: parseFloat(r.lng),
            description: r.description || "No description available",
            visitors: r.visitors || 0,
            tags: r.tags ? r.tags.split(",").map((t) => t.trim()) : [],
            image: r.image
              ? `/images/${r.image.replace(/^\/+/, "").split("/").pop()}`
              : null,
          }));
        setSpots(rows);
      })
      .catch((err) => console.error(err));
  }, []);

  // Fetch user's itineraries
  useEffect(() => {
    const token = localStorage.getItem("token");
    if (!token) return;

    fetch("http://localhost:5000/api/itineraries", {
      headers: { Authorization: `Bearer ${token}` },
    })
      .then((res) => res.json())
      .then((data) => {
        if (data.itineraries) {
          const pending = data.itineraries.find((it) => it.status === "pending");
          setPendingItinerary(pending || null);
        }
      })
      .catch((err) => console.error("Error fetching itineraries:", err));
  }, []);

  // Live time
  useEffect(() => {
    const timer = setInterval(() => setCurrentTime(new Date()), 1000);
    return () => clearInterval(timer);
  }, []);

  const getAdvisory = () => {
    switch (weather.toLowerCase()) {
      case "sunny":
        return "Perfect for outdoor trips! Don‚Äôt forget sunscreen and water. ‚òÄÔ∏è";
      case "rainy":
        return "Take precautions! Bring umbrella or raincoat. üåßÔ∏è";
      case "cloudy":
        return "Mild weather ‚Äî nice for sightseeing but stay alert for rain. ‚òÅÔ∏è";
      case "windy":
        return "Secure belongings and avoid high areas. üí®";
      default:
        return "Enjoy your trip and stay safe!";
    }
  };

  const top10Spots = [...spots]
    .sort((a, b) => (b.visitors || 0) - (a.visitors || 0))
    .slice(0, 10);

  const focusOnSpot = (spot) => {
    if (mapRef.current)
      mapRef.current.flyTo([spot.lat, spot.lng], 15, { duration: 1.5 });
    if (markerRefs.current[spot.name])
      markerRefs.current[spot.name].openPopup();
  };

  const goToRouteDetails = (itinerary = null) => {
    if (itinerary) {
      navigate("/routedetails", { state: { itinerary } });
    } else if (route.length) {
      navigate("/routedetails", { state: { itinerary: route } });
    } else {
      alert("No route generated!");
    }
  };

  return (
    <div
      style={{
        padding: "30px",
        backgroundColor: "#f3f4f6",
        minHeight: "100vh",
        fontFamily: "Arial, sans-serif",
      }}
    >
      {/* Weather and Route Planner Section */}
      <div
        style={{
          background: "#fff",
          borderRadius: "16px",
          padding: "20px",
          boxShadow: "0 4px 12px rgba(0,0,0,0.1)",
          marginBottom: "25px",
        }}
      >
        <WeatherWidget setWeather={setWeather} />

        {/* Time + Advisory */}
        <div
          style={{
            backgroundColor: "#f9fafb",
            padding: "15px 20px",
            borderRadius: "10px",
            marginTop: "15px",
            color: "#1e3a8a",
            fontWeight: "500",
            display: "flex",
            justifyContent: "space-between",
            alignItems: "center",
            flexWrap: "wrap",
          }}
        >
          <div style={{ flex: "1", minWidth: "220px" }}>
            üå§Ô∏è <strong>Weather Advisory:</strong> {getAdvisory()}
          </div>
          <div
            style={{
              backgroundColor: "#fff",
              borderRadius: "10px",
              padding: "8px 14px",
              fontSize: "15px",
              color: "#1e40af",
              fontWeight: "bold",
              boxShadow: "0 2px 5px rgba(0,0,0,0.1)",
              marginTop: "10px",
            }}
          >
            {currentTime.toLocaleString("en-PH", {
              weekday: "long",
              hour: "2-digit",
              minute: "2-digit",
              second: "2-digit",
            })}
          </div>
        </div>

        {/* Route Planner */}
        <RoutePlanner spots={spots} setRoute={setRoute} weather={weather} />

        {/* Route buttons side by side */}
        <div
          style={{
            display: "flex",
            gap: "10px",
            marginTop: "16px",
          }}
        >
          {route.length > 0 && (
            <button
              onClick={() => goToRouteDetails()}
              style={{
                padding: "12px 20px",
                backgroundColor: "#2563eb",
                color: "white",
                fontSize: "16px",
                border: "none",
                borderRadius: "8px",
                cursor: "pointer",
                transition: "background-color 0.3s",
                flex: "1",
              }}
              onMouseOver={(e) => (e.target.style.backgroundColor = "#1d4ed8")}
              onMouseOut={(e) => (e.target.style.backgroundColor = "#2563eb")}
            >
              View Recommended Route
            </button>
          )}

          {pendingItinerary ? (
            <button
              onClick={() => goToRouteDetails(pendingItinerary.route_data)}
              style={{
                padding: "12px 20px",
                backgroundColor: "#10b981",
                color: "white",
                fontSize: "16px",
                border: "none",
                borderRadius: "8px",
                cursor: "pointer",
                transition: "background-color 0.3s",
                flex: "1",
              }}
              onMouseOver={(e) => (e.target.style.backgroundColor = "#059669")}
              onMouseOut={(e) => (e.target.style.backgroundColor = "#10b981")}
            >
              View Pending Itinerary
            </button>
          ) : (
            <button
              disabled
              style={{
                padding: "12px 20px",
                backgroundColor: "#9ca3af",
                color: "white",
                fontSize: "16px",
                border: "none",
                borderRadius: "8px",
                cursor: "not-allowed",
                flex: "1",
              }}
            >
              No Pending Itinerary
            </button>
          )}
        </div>
      </div>

      {/* Map + Top Spots */}
      <div
        style={{
          display: "grid",
          gridTemplateColumns: "1.5fr 1fr",
          gap: "25px",
        }}
      >
        {/* Map */}
        <div
          style={{
            background: "#fff",
            borderRadius: "16px",
            padding: "16px",
            boxShadow: "0 4px 12px rgba(0,0,0,0.1)",
            height: "600px",
          }}
        >
          <h2
            style={{
              fontSize: "22px",
              fontWeight: "bold",
              marginBottom: "10px",
              color: "#1e3a8a",
            }}
          >
            Explore Map
          </h2>

          {top10Spots.length > 0 ? (
            <MapContainer
              center={CENTER}
              zoom={13}
              style={{
                height: "90%",
                width: "100%",
                borderRadius: "12px",
              }}
              whenCreated={(mapInstance) => (mapRef.current = mapInstance)}
            >
              <TileLayer
                attribution='&copy; <a href="https://www.openstreetmap.org/">OSM contributors</a>'
                url="https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png"
              />
              {top10Spots.map((spot, i) => (
                <Marker
                  key={i}
                  position={[spot.lat, spot.lng]}
                  ref={(ref) => {
                    if (ref) markerRefs.current[spot.name] = ref;
                  }}
                >
                  <Popup>
                    <strong>{spot.name}</strong>
                    <br />
                    Visitors: {spot.visitors}
                    {spot.image && <br />}
                    {spot.image && (
                      <img
                        src={spot.image}
                        alt={spot.name}
                        style={{
                          width: "160px",
                          borderRadius: "10px",
                          marginTop: 6,
                        }}
                      />
                    )}
                    <p style={{ marginTop: "8px", color: "#555" }}>
                      {spot.description}
                    </p>
                  </Popup>
                </Marker>
              ))}
            </MapContainer>
          ) : (
            <div
              style={{
                textAlign: "center",
                color: "#666",
                fontSize: "18px",
                padding: "40px",
              }}
            >
              No spots available
            </div>
          )}
        </div>

        {/* Top Spots List */}
        <div
          style={{
            background: "#fff",
            borderRadius: "16px",
            padding: "20px",
            boxShadow: "0 4px 12px rgba(0,0,0,0.1)",
            overflowY: "auto",
            maxHeight: "600px",
          }}
        >
          <h2
            style={{
              fontSize: "22px",
              fontWeight: "bold",
              marginBottom: "16px",
              color: "#1e3a8a",
            }}
          >
            Top 10 Tourist Spots
          </h2>

          <ul style={{ listStyle: "none", padding: 0, margin: 0 }}>
            {top10Spots.map((spot, i) => (
              <li
                key={i}
                style={{
                  marginBottom: "14px",
                  padding: "12px 14px",
                  backgroundColor: "#f9fafb",
                  borderRadius: "8px",
                  cursor: "pointer",
                  transition: "background-color 0.3s",
                }}
                onClick={() => focusOnSpot(spot)}
                onMouseOver={(e) =>
                  (e.currentTarget.style.backgroundColor = "#e0e7ff")
                }
                onMouseOut={(e) =>
                  (e.currentTarget.style.backgroundColor = "#f9fafb")
                }
              >
                <strong style={{ color: "#1e40af" }}>{spot.name}</strong>
                <br />
                <span style={{ color: "#555" }}>
                  Visitors: {spot.visitors.toLocaleString()}
                </span>
              </li>
            ))}
          </ul>
        </div>
      </div>
    </div>
  );
}
