import React, { useEffect, useState } from "react";
import { MapContainer, TileLayer, Marker, Popup } from "react-leaflet";
import "leaflet/dist/leaflet.css";
import "../leafletfix"; // fix leaflet icons
import Papa from "papaparse";

const CENTER = { lat: 14.1467, lng: 121.4708 };

export default function MapPage() {
  const [spots, setSpots] = useState([]);
  const [selectedSpot, setSelectedSpot] = useState(null);
  const [minimized, setMinimized] = useState(false);

  useEffect(() => {
    Papa.parse("/spots.csv", {
      download: true,
      header: true,
      complete: (res) => {
        const rows = res.data
          .filter((r) => r.name && r.lat && r.lng)
          .map((r) => ({
            name: r.name,
            lat: parseFloat(r.lat),
            lng: parseFloat(r.lng),
            description: r.description || "No description available.",
            tags: (r.tags || "")
              .split(",")
              .map((t) => t.trim())
              .filter(Boolean),
            visitors: r.visitors || "Moderate",
          }));
        setSpots(rows);
      },
    });
  }, []);

  return (
    <div className="card" style={{ padding: 0, position: "relative", height: "90vh" }}>
      {/* Map */}
      <div className={selectedSpot ? "map-small" : "map-full"} style={{ height: "100%", transition: "0.3s" }}>
        <MapContainer center={CENTER} zoom={13} style={{ height: "100%", width: "100%" }}>
          <TileLayer
            attribution='&copy; <a href="https://www.openstreetmap.org/">OSM contributors</a>'
            url="https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png"
          />
          {spots.map((s, i) => (
            <Marker
              key={i}
              position={[s.lat, s.lng]}
              eventHandlers={{
                click: () => {
                  setSelectedSpot(s);
                  setMinimized(false);
                },
              }}
            />
          ))}
        </MapContainer>
      </div>

      {/* Info Panel */}
      {selectedSpot && (
        <div
          style={{
            position: "absolute",
            bottom: 0,
            left: 0,
            right: 0,
            background: "#fff",
            borderTop: "2px solid #ccc",
            padding: "10px",
            height: minimized ? "40px" : "200px",
            transition: "0.3s",
            overflowY: "auto",
          }}
        >
          <div style={{ display: "flex", justifyContent: "space-between" }}>
            <strong>{selectedSpot.name}</strong>
            <button onClick={() => setMinimized(!minimized)}>
              {minimized ? "Maximize" : "Minimize"}
            </button>
          </div>
          {!minimized && (
            <>
              <p>{selectedSpot.description}</p>
              {selectedSpot.tags?.length > 0 && <p><b>Tags:</b> {selectedSpot.tags.join(", ")}</p>}
              <p><b>Visitors:</b> {selectedSpot.visitors}</p>
            </>
          )}
        </div>
      )}
    </div>
  );
}
