import React, { useEffect, useState } from "react";
import { MapContainer, TileLayer, Marker } from "react-leaflet";
import "leaflet/dist/leaflet.css";
import L from "leaflet";

delete L.Icon.Default.prototype._getIconUrl;
L.Icon.Default.mergeOptions({
  iconRetinaUrl: require("leaflet/dist/images/marker-icon-2x.png"),
  iconUrl: require("leaflet/dist/images/marker-icon.png"),
  shadowUrl: require("leaflet/dist/images/marker-shadow.png"),
});

const CENTER = { lat: 14.1467, lng: 121.4708 };

export default function MapPage() {
  const [spots, setSpots] = useState([]);
  const [selectedSpot, setSelectedSpot] = useState(null);

  useEffect(() => {
    fetch("http://localhost:5000/api/pois")
      .then((res) => res.json())
      .then((data) => {
        const rows = data
          .filter((r) => r.name && r.lat && r.lng)
          .map((r) => ({
            id: r.id,
            name: r.name,
            lat: parseFloat(r.lat),
            lng: parseFloat(r.lng),
            description: r.description || "No description available",
            visitors: r.visitors || "Moderate",
            tags: r.tags ? r.tags.split(",").map((t) => t.trim()) : [],
            image: r.image
              ? `/images/${r.image.replace(/^\/+/, "").split("/").pop()}`
              : null,
          }));
        setSpots(rows);
      })
      .catch((err) => console.error("Error fetching spots:", err));
  }, []);

  return (
    <div style={{ display: "flex", height: "90vh", width: "100%" }}>
      <div style={{ flex: 1 }}>
        <MapContainer
          center={spots.length ? [spots[0].lat, spots[0].lng] : [CENTER.lat, CENTER.lng]}
          zoom={13}
          style={{ height: "100%", width: "100%" }}
        >
          <TileLayer
            attribution='&copy; <a href="https://www.openstreetmap.org/">OSM contributors</a>'
            url="https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png"
          />
          {spots.map((spot) => (
            <Marker
              key={spot.id}
              position={[spot.lat, spot.lng]}
              eventHandlers={{ click: () => setSelectedSpot(spot) }}
            />
          ))}
        </MapContainer>
      </div>

      {selectedSpot && (
        <div
          style={{
            width: "40%",
            background: "#fff",
            padding: "20px",
            overflowY: "auto",
            borderLeft: "2px solid #e5e7eb",
            boxShadow: "-3px 0 8px rgba(0,0,0,0.1)",
            animation: "fadeIn 0.4s ease-in-out",
          }}
        >
          <button
            onClick={() => setSelectedSpot(null)}
            style={{
              float: "right",
              background: "#ef4444",
              color: "white",
              border: "none",
              borderRadius: "6px",
              padding: "6px 12px",
              cursor: "pointer",
              fontWeight: "bold",
            }}
          >
            Close
          </button>

          {selectedSpot.image && (
            <img
              src={selectedSpot.image}
              alt={selectedSpot.name}
              style={{
                width: "100%",
                height: "280px",
                objectFit: "cover",
                borderRadius: "12px",
                marginBottom: "18px",
                transition: "transform 0.3s ease",
                boxShadow: "0 4px 12px rgba(0,0,0,0.15)",
              }}
              onMouseOver={(e) =>
                (e.currentTarget.style.transform = "scale(1.03)")}
              onMouseOut={(e) =>
                (e.currentTarget.style.transform = "scale(1)")}
            />
          )}

          <h2
            style={{
              fontSize: "1.8rem",
              fontWeight: "700",
              marginBottom: "10px",
              color: "#1f2937",
            }}
          >
            {selectedSpot.name}
          </h2>

          <p style={{ fontSize: "1rem", color: "#374151", marginBottom: "12px" }}>
            <strong>Visitors:</strong> {selectedSpot.visitors}
          </p>

          <p style={{ fontSize: "1rem", color: "#4b5563", lineHeight: "1.6", marginBottom: "14px" }}>
            {selectedSpot.description}
          </p>

          {selectedSpot.tags.length > 0 && (
            <p
              style={{
                marginTop: "12px",
                padding: "10px",
                background: "#f3f4f6",
                borderRadius: "6px",
                fontSize: "0.95rem",
                color: "#374151",
              }}
            >
              <strong>Tags:</strong> {selectedSpot.tags.join(", ")}
            </p>
          )}
        </div>
      )}

      <style>
        {`
          @keyframes fadeIn {
            from { opacity: 0; transform: translateX(20px); }
            to { opacity: 1; transform: translateX(0); }
          }
        `}
      </style>
    </div>
  );
}
