import React, { useEffect, useState } from "react";
import Papa from "papaparse";

export default function NatureCulturePage() {
  const [places, setPlaces] = useState([]);
  const [expandedIndex, setExpandedIndex] = useState(null);

  useEffect(() => {
    fetch("/data/spots.csv")
      .then((res) => res.text())
      .then((csv) => {
        Papa.parse(csv, {
          header: true,
          skipEmptyLines: true,
          complete: (res) => {
            const rows = res.data
              .filter((r) => r.name && r.tags && r.tags.includes("nature, culture"))
              .map((r) => ({ ...r, image: r.image || null }));
            setPlaces(rows);
          },
        });
      });
  }, []);

  const toggleExpand = (index) => {
    setExpandedIndex(expandedIndex === index ? null : index);
  };

  return (
    <div className="p-6">
      <h1 className="text-2xl font-bold mb-6">Nature & Culture</h1>
      <div className="grid grid-cols-1 sm:grid-cols-2 md:grid-cols-3 gap-6">
        {places.map((p, i) => (
          <div
            key={i}
            className={`cursor-pointer rounded-2xl shadow-md border bg-white overflow-hidden transition-all ${
              expandedIndex === i ? "md:col-span-3" : ""
            }`}
            onClick={() => toggleExpand(i)}
          >
            {p.image ? (
              <img
                src={p.image}
                alt={p.name}
                className={`w-full object-cover transition-all ${
                  expandedIndex === i ? "h-80" : "h-48"
                }`}
              />
            ) : (
              <div
                className={`w-full flex items-center justify-center bg-gray-100 text-gray-500 transition-all ${
                  expandedIndex === i ? "h-80" : "h-48"
                }`}
              >
                No image
              </div>
            )}
            <div className="p-3">
              <h3 className="text-lg font-bold">{p.name}</h3>
              {expandedIndex === i && (
                <div className="mt-2 text-gray-700">
                  <p>{p.description}</p>
                  <p className="mt-1 text-sm text-gray-500">Tags: {p.tags}</p>
                  <p className="mt-1 text-sm text-gray-500">
                    Open Hours: {p.open_hours || "Not specified"}
                  </p>
                  <p className="mt-1 text-sm text-gray-500">
                    Visitors: {p.visitors || "N/A"}
                  </p>
                </div>
              )}
            </div>
          </div>
        ))}
      </div>
    </div>
  );
}
