import React, { useEffect, useState, useRef } from "react";
import { MapContainer, TileLayer, Marker, Popup, Polyline } from "react-leaflet";
import "leaflet/dist/leaflet.css";
import L from "leaflet";
import { useNavigate, useLocation } from "react-router-dom";

export default function RouteDetails() {
  const location = useLocation();
  const navigate = useNavigate();
  const routeSpots = location.state?.itinerary || [];

  const [segments, setSegments] = useState([]);
  const [loading, setLoading] = useState(true);
  const [poiDetails, setPoiDetails] = useState([]);
  const [saving, setSaving] = useState(false);
  const [saveMessage, setSaveMessage] = useState("");
  const [status, setStatus] = useState("pending");
  const [itineraryId, setItineraryId] = useState(null);

  const mapRef = useRef(null);

  const ORS_API_KEY =
    "eyJvcmciOiI1YjNjZTM1OTc4NTExMTAwMDFjZjYyNDgiLCJpZCI6ImJhNWU3NmIxOTRmMzQyYTg4NzY4MDc2MzVmZDY1ZDRhIiwiaCI6Im11cm11cjY0In0=";

  const numberedIcon = (number) =>
    L.divIcon({
      html: `<div style="
        background:#1976d2;
        color:#fff;
        width:28px;
        height:28px;
        border-radius:50%;
        text-align:center;
        line-height:28px;
        font-weight:600;
        border:2px solid #fff;
        box-shadow:0 2px 6px rgba(0,0,0,0.3);
      ">${number}</div>`,
      className: "",
      iconSize: [28, 28],
    });

  // Fetch POI details
  const fetchPoiDetails = async () => {
    try {
      const res = await fetch("http://localhost:5000/api/pois");
      const data = await res.json();
      const matched = routeSpots.map((spot) =>
        data.find((poi) => poi.name === spot.name || poi.id === spot.id)
      );
      setPoiDetails(matched);
    } catch (err) {
      console.error("Error fetching POI details:", err);
    }
  };

  const fetchRouteSegment = async (from, to) => {
    if (!from || !to) return null;
    try {
      const res = await fetch("https://api.openrouteservice.org/v2/directions/driving-car/geojson", {
        method: "POST",
        headers: { Authorization: ORS_API_KEY, "Content-Type": "application/json" },
        body: JSON.stringify({
          coordinates: [
            [from.lng, from.lat],
            [to.lng, to.lat],
          ],
          preference: "fastest",
          instructions: false,
        }),
      });
      const data = await res.json();
      if (data.features?.length) {
        const coords = data.features[0].geometry.coordinates.map(([lng, lat]) => [lat, lng]);
        const summary = data.features[0].properties.summary;
        return { coords, summary };
      }
    } catch {
      return { coords: [[from.lat, from.lng], [to.lat, to.lng]], summary: { distance: 0, duration: 0 } };
    }
  };

  useEffect(() => {
    const loadAllSegments = async () => {
      setLoading(true);
      await fetchPoiDetails();
      const segs = [];
      // Only connect waypoints, not the user's location
      for (let i = 0; i < routeSpots.length - 1; i++) {
        const seg = await fetchRouteSegment(routeSpots[i], routeSpots[i + 1]);
        if (seg) segs.push(seg);
      }
      setSegments(segs);
      setLoading(false);
    };
    if (routeSpots.length > 1) loadAllSegments();
  }, [routeSpots]);

  const handleZoomToPoi = (poi) => {
    if (mapRef.current) mapRef.current.setView([poi.lat, poi.lng], 16, { animate: true });
  };

  const formatDistance = (m) => `${(m / 1000).toFixed(2)} km`;
  const formatDuration = (s) => `${Math.floor(s / 60)} min`;

  const handleSaveItinerary = async () => {
    const token = localStorage.getItem("token");
    if (!token) return alert("Please log in to save itineraries.");
    setSaving(true); setSaveMessage("");
    try {
      const res = await fetch("http://localhost:5000/api/itineraries", {
        method: "POST",
        headers: { "Content-Type": "application/json", Authorization: `Bearer ${token}` },
        body: JSON.stringify({ name: `Route - ${new Date().toLocaleDateString()}`, route_data: routeSpots, status: "pending" }),
      });
      const data = await res.json();
      if (res.ok) { setSaveMessage("‚úÖ Itinerary saved successfully!"); setItineraryId(data.id); setStatus("pending"); }
      else setSaveMessage(`‚ùå Failed to save: ${data.message || "Unknown error"}`);
    } catch { setSaveMessage("‚ùå Server error while saving itinerary."); }
    finally { setSaving(false); setTimeout(() => setSaveMessage(""), 4000); }
  };

  const updateStatus = async (newStatus) => {
    if (!itineraryId) return alert("Please save itinerary first.");
    const token = localStorage.getItem("token");
    if (!token) return alert("Please log in to update status.");
    try {
      const res = await fetch(`http://localhost:5000/api/itineraries/${itineraryId}/status`, {
        method: "PUT",
        headers: { "Content-Type": "application/json", Authorization: `Bearer ${token}` },
        body: JSON.stringify({ status: newStatus }),
      });
      const data = await res.json();
      if (res.ok) { setStatus(newStatus); setSaveMessage(`‚úÖ Itinerary marked as ${newStatus}.`); }
      else setSaveMessage(`‚ùå Failed to update: ${data.message || "Unknown error"}`);
    } catch { setSaveMessage("‚ùå Server error while updating status."); }
    finally { setTimeout(() => setSaveMessage(""), 4000); }
  };

  if (!routeSpots.length) return <p>Loading route...</p>;

  return (
    <div style={{ display: "grid", gridTemplateColumns: "1.3fr 0.7fr", gap: "24px", padding: "24px", minHeight: "100vh", fontFamily: "'Poppins', sans-serif", background: "linear-gradient(to bottom right, #e3f2fd, #e8f5e9)" }}>
      
      {/* Map */}
      <div style={{ height: "650px", borderRadius: "18px", overflow: "hidden", boxShadow: "0 10px 25px rgba(0,0,0,0.15)", border: "4px solid #fff" }}>
        <MapContainer ref={mapRef} center={[routeSpots[0].lat, routeSpots[0].lng]} zoom={13} style={{ height: "100%", width: "100%" }}>
          <TileLayer url="https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png" attribution="¬© OpenStreetMap" />
          {segments.map((seg, i) => <Polyline key={i} positions={seg.coords} color="#1976d2" weight={5} opacity={0.9} />)}
          {routeSpots.map((poi, i) => (
            <Marker key={i} position={[poi.lat, poi.lng]} icon={numberedIcon(i + 1)}>
              <Popup>
                <strong>Stop {i + 1}: {poiDetails[i]?.name || poi.name}</strong>
                <p>{poiDetails[i]?.description || "No description available."}</p>
                {poiDetails[i]?.image && <img src={`http://localhost:5000/images/${poiDetails[i].image}`} alt={poi.name} style={{ width: "160px", borderRadius: "8px", marginTop: "6px" }} />}
                <p style={{ fontSize: "0.85rem", marginTop: "8px" }}>üë• Visitors: {poiDetails[i]?.visitors || "N/A"}<br />üè∑ Tags: {poiDetails[i]?.tags || "None"}</p>
              </Popup>
            </Marker>
          ))}
        </MapContainer>
      </div>

      {/* Sidebar */}
      <div style={{ background: "#fff", borderRadius: "18px", padding: "24px", boxShadow: "0 8px 24px rgba(0,0,0,0.1)", border: "1px solid #e0e0e0", overflowY: "auto", maxHeight: "650px" }}>
        <h2 style={{ color: "#1565c0", fontWeight: "700", marginBottom: "16px" }}>Route Overview</h2>
        <p style={{ background: status === "finished" ? "#d1fae5" : status === "cancelled" ? "#fee2e2" : "#fef9c3", color: status === "finished" ? "#065f46" : status === "cancelled" ? "#991b1b" : "#92400e", padding: "8px 12px", borderRadius: "8px", fontWeight: "600", marginBottom: "16px" }}>
          Status: {status.toUpperCase()}
        </p>

        {poiDetails.map((poi, i) => {
          const seg = segments[i];
          return (
            <div key={i} onClick={() => handleZoomToPoi(routeSpots[i])} style={{ marginBottom: "20px", borderBottom: "1px solid #e0e0e0", paddingBottom: "16px", cursor: "pointer" }}>
              <h3 style={{ color: "#1976d2", fontWeight: "600" }}>Stop {i + 1}: {poi?.name}</h3>
              <p style={{ color: "#444" }}>{poi?.description}</p>
              {poi?.image && <img src={`http://localhost:5000/images/${poi.image}`} alt={poi.name} style={{ width: "100%", maxWidth: "280px", borderRadius: "10px", boxShadow: "0 6px 12px rgba(0,0,0,0.1)", marginBottom: "8px" }} />}
              <p style={{ margin: "6px 0" }}><strong>Visitors:</strong> {poi.visitors || "N/A"}<br /><strong>Tags:</strong> {poi.tags || "None"}</p>
              {seg && <p style={{ margin: 0 }}><strong>To next stop:</strong> {formatDistance(seg.summary.distance)}, {formatDuration(seg.summary.duration)}</p>}
            </div>
          );
        })}

        {status === "pending" ? (
          <>
            <button onClick={handleSaveItinerary} disabled={saving} style={{ width: "100%", padding: "12px 20px", background: saving ? "#ccc" : "#1976d2", color: "#fff", border: "none", borderRadius: "8px", fontWeight: "600", cursor: "pointer", marginTop: "12px" }}>{saving ? "Saving..." : "üíæ Save This Route"}</button>
            <button onClick={() => updateStatus("finished")} style={{ width: "100%", padding: "12px 20px", background: "#4caf50", color: "#fff", border: "none", borderRadius: "8px", fontWeight: "600", cursor: "pointer", marginTop: "10px" }}>‚úÖ Mark as Finished</button>
            <button onClick={() => updateStatus("cancelled")} style={{ width: "100%", padding: "12px 20px", background: "#f44336", color: "#fff", border: "none", borderRadius: "8px", fontWeight: "600", cursor: "pointer", marginTop: "10px" }}>‚ùå Cancel Itinerary</button>
          </>
        ) : (
          <button onClick={() => navigate("/home")} style={{ width: "100%", padding: "12px 20px", background: "#9e9e9e", color: "#fff", border: "none", borderRadius: "8px", fontWeight: "600", cursor: "pointer", marginTop: "10px" }}>Back to Home</button>
        )}

        {saveMessage && <p style={{ marginTop: "10px", textAlign: "center", color: "#1565c0" }}>{saveMessage}</p>}
      </div>
    </div>
  );
}
