import React, { useEffect, useState } from "react";
import Papa from "papaparse";

function EstablishmentsPage() {
  const [lodgings, setLodgings] = useState([]);
  const [restaurants, setRestaurants] = useState([]);

  useEffect(() => {
    fetch("/data/spots.csv")
      .then(res => res.text())
      .then(csv => {
        Papa.parse(csv, {
          header: true,
          skipEmptyLines: true,
          complete: function(res) {
            var rows = res.data
              .filter(r => r.type && r.name)
              .map(r => ({
                ...r,
                image: r.image ? "/images/" + r.image.replace(/^\//, "") : null
              }));

            setLodgings(rows.filter(r =>
              ["Lodging","Resort","Motel","Hotel"].includes(r.type)
            ));
            setRestaurants(rows.filter(r =>
              ["Restaurant","Coffee","Cafe"].includes(r.type)
            ));
          }
        });
      });
  }, []);

  function renderCards(items) {
    return React.createElement(
      "div",
      { className: "grid grid-cols-1 sm:grid-cols-2 md:grid-cols-3 gap-6" },
      items.map(function(item, i) {
        return React.createElement(
          "div",
          { key: i, className: "bg-white rounded-2xl shadow-md overflow-hidden border hover:shadow-lg transition" },
          item.image
            ? React.createElement("img", { src: item.image, alt: item.name, className: "h-48 w-full object-cover" })
            : React.createElement(
                "div",
                { className: "h-48 w-full flex items-center justify-center bg-gray-100 text-gray-500" },
                "No image"
              ),
          React.createElement(
            "div",
            { className: "p-3" },
            React.createElement("h3", { className: "text-lg font-bold" }, item.name),
            item.description ? React.createElement("p", { className: "text-sm text-gray-600" }, item.description) : null,
            item.tags ? React.createElement("p", { className: "text-xs text-gray-400 mt-2" }, item.tags) : null
          )
        );
      })
    );
  }

  return React.createElement(
    "div",
    { className: "p-6" },
    React.createElement("h1", { className: "text-2xl font-bold mb-6" }, "Establishments"),
    React.createElement(
      "section",
      { className: "mb-10" },
      React.createElement("h2", { className: "text-xl font-semibold mb-4" }, "Lodgings"),
      lodgings.length ? renderCards(lodgings) : React.createElement("p", null, "No lodgings found.")
    ),
    React.createElement(
      "section",
      null,
      React.createElement("h2", { className: "text-xl font-semibold mb-4" }, "Restaurants"),
      restaurants.length ? renderCards(restaurants) : React.createElement("p", null, "No restaurants found.")
    )
  );
}

export default EstablishmentsPage;
