import React, { useEffect, useState } from "react";

export default function ProfilePage() {
  const storedUser = JSON.parse(localStorage.getItem("user"));
  const token = localStorage.getItem("token");

  const [user, setUser] = useState(null);
  const [preferences, setPreferences] = useState("");
  const [editing, setEditing] = useState(false);

  useEffect(() => {
    if (!storedUser) return;

    fetch(`http://localhost:5000/api/users/email/${storedUser.email}`, {
      headers: { Authorization: `Bearer ${token}` },
    })
      .then((res) => {
        if (!res.ok) throw new Error("Failed to fetch profile");
        return res.json();
      })
      .then((data) => {
        setUser(data);
        setPreferences(data.preferences || "");
      })
      .catch((err) => console.error("Error fetching user:", err));
  }, [storedUser, token]);

  const handleSave = () => {
    if (!user) return;

    fetch(`http://localhost:5000/api/users/${user.id}/preferences`, {
      method: "PUT",
      headers: {
        "Content-Type": "application/json",
        Authorization: `Bearer ${token}`,
      },
      body: JSON.stringify({ preferences }),
    })
      .then((res) => {
        if (!res.ok) throw new Error("Failed to update preferences");
        return res.json();
      })
      .then(() => {
        setEditing(false);
        setUser({ ...user, preferences });
      })
      .catch((err) => console.error("Error updating preferences:", err));
  };

  if (!user) return <div style={{ padding: "24px" }}>Loading...</div>;

  return (
    <div style={{ padding: "24px", maxWidth: "600px", margin: "0 auto" }}>
      <h1 style={{ fontSize: "1.8rem", fontWeight: "bold", marginBottom: "16px" }}>
        Profile
      </h1>

      <div
        style={{
          border: "1px solid #ddd",
          borderRadius: "16px",
          padding: "20px",
          background: "#fff",
          boxShadow: "0 2px 6px rgba(0,0,0,0.1)",
        }}
      >
        <p><strong>Username:</strong> {user.username}</p>
        <p><strong>Email:</strong> {user.email}</p>

        <div style={{ marginTop: "16px" }}>
          <strong>Preferences:</strong>
          {editing ? (
            <div>
              <textarea
                value={preferences}
                onChange={(e) => setPreferences(e.target.value)}
                style={{
                  width: "100%",
                  height: "80px",
                  marginTop: "8px",
                  padding: "8px",
                  border: "1px solid #ddd",
                  borderRadius: "8px",
                }}
              />
              <div style={{ marginTop: "8px" }}>
                <button onClick={handleSave} style={{ background: "#007bff", color: "#fff", padding: "8px 16px", border: "none", borderRadius: "8px" }}>
                  Save
                </button>
                <button
                  onClick={() => {
                    setEditing(false);
                    setPreferences(user.preferences || "");
                  }}
                  style={{ background: "#ccc", padding: "8px 16px", border: "none", borderRadius: "8px", marginLeft: "8px" }}
                >
                  Cancel
                </button>
              </div>
            </div>
          ) : (
            <p style={{ marginTop: "8px" }}>{user.preferences || "No preferences set."}</p>
          )}
        </div>

        {!editing && (
          <button
            onClick={() => setEditing(true)}
            style={{ marginTop: "16px", background: "#28a745", color: "#fff", padding: "8px 16px", border: "none", borderRadius: "8px" }}
          >
            Edit Preferences
          </button>
        )}
      </div>
    </div>
  );
}
