import React, { useEffect, useState, useRef } from "react";
import { MapContainer, TileLayer, Marker, Popup } from "react-leaflet";
import "leaflet/dist/leaflet.css";
import { useNavigate } from "react-router-dom";
import RoutePlanner from "../components/RoutePlanner";
import WeatherWidget from "../components/WeatherWidget";

const CENTER = { lat: 14.1467, lng: 121.4708 };

export default function Home() {
  const [spots, setSpots] = useState([]);
  const [route, setRoute] = useState([]);
  const [weather, setWeather] = useState("Sunny");
  const mapRef = useRef(null);
  const markerRefs = useRef({});
  const navigate = useNavigate();

  useEffect(() => {
    fetch("http://localhost:5000/api/pois")
      .then((res) => res.json())
      .then((data) => {
        const rows = data
          .filter((r) => r.name && r.lat && r.lng)
          .map((r) => ({
            name: r.name,
            lat: parseFloat(r.lat),
            lng: parseFloat(r.lng),
            description: r.description || "No description available",
            visitors: r.visitors || 0,
            tags: r.tags ? r.tags.split(",").map((t) => t.trim()) : [],
            image: r.image
              ? `/images/${r.image.replace(/^\/+/, "").split("/").pop()}`
              : null,
          }));
        setSpots(rows);
      })
      .catch((err) => console.error(err));
  }, []);

  // Get top 10 spots by visitors
  const top10Spots = [...spots]
    .sort((a, b) => (b.visitors || 0) - (a.visitors || 0))
    .slice(0, 10);

  const focusOnSpot = (spot) => {
    if (mapRef.current) {
      mapRef.current.flyTo([spot.lat, spot.lng], 15, { duration: 1.5 });
    }
    if (markerRefs.current[spot.name]) {
      markerRefs.current[spot.name].openPopup();
    }
  };

  const goToRouteDetails = () => {
    if (!route.length) return alert("No route generated!");
    navigate("/routedetails", { state: { itinerary: route } });
  };

  return (
    <div style={{ padding: 16 }}>
      <WeatherWidget setWeather={setWeather} />
      <RoutePlanner spots={spots} setRoute={setRoute} weather={weather} />
      {route.length > 0 && (
        <button
          onClick={goToRouteDetails}
          style={{
            marginTop: 12,
            padding: "10px 16px",
            backgroundColor: "#3b82f6",
            color: "#fff",
            border: "none",
            borderRadius: "8px",
            cursor: "pointer",
          }}
        >
          View Recommended Route
        </button>
      )}

      <div
        style={{
          display: "grid",
          gridTemplateColumns: "1fr 1fr",
          gap: 20,
          marginTop: 20,
        }}
      >
        {/* Map */}
        <div
          style={{
            background: "#fff",
            borderRadius: 12,
            padding: 16,
            boxShadow: "0 4px 10px rgba(0,0,0,0.1)",
            height: "500px",
          }}
        >
          {top10Spots.length > 0 ? (
            <MapContainer
              center={CENTER}
              zoom={13}
              style={{ height: "100%", width: "100%" }}
              whenCreated={(mapInstance) => (mapRef.current = mapInstance)}
            >
              <TileLayer
                attribution='&copy; <a href="https://www.openstreetmap.org/">OSM contributors</a>'
                url="https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png"
              />
              {top10Spots.map((spot, i) => (
                <Marker
                  key={i}
                  position={[spot.lat, spot.lng]}
                  ref={(ref) => {
                    if (ref) markerRefs.current[spot.name] = ref;
                  }}
                >
                  <Popup>
                    <strong>{spot.name}</strong>
                    <br />
                    Visitors: {spot.visitors}
                    {spot.image && <br />}
                    {spot.image && (
                      <img
                        src={spot.image}
                        alt={spot.name}
                        style={{
                          width: "150px",
                          borderRadius: "8px",
                          marginTop: 6,
                        }}
                      />
                    )}
                    <p>{spot.description}</p>
                  </Popup>
                </Marker>
              ))}
            </MapContainer>
          ) : (
            <div
              style={{ textAlign: "center", color: "#888", padding: 20 }}
            >
              No spots available
            </div>
          )}
        </div>

        {/* Top Places List */}
        <div
          style={{
            background: "#fff",
            borderRadius: 12,
            padding: 16,
            boxShadow: "0 4px 10px rgba(0,0,0,0.1)",
          }}
        >
          <h2 style={{ fontSize: 20, fontWeight: "bold", marginBottom: 12 }}>
            Top Places
          </h2>
          <ul style={{ paddingLeft: 20, color: "#444" }}>
            {top10Spots.map((spot, i) => (
              <li
                key={i}
                style={{ marginBottom: 8, cursor: "pointer" }}
                onClick={() => focusOnSpot(spot)}
              >
                <strong>{spot.name}</strong> â€” {spot.visitors} visitors
              </li>
            ))}
          </ul>
        </div>
      </div>
    </div>
  );
}
