import React, { useEffect, useState, useRef } from "react";
import { MapContainer, TileLayer, Marker, Popup } from "react-leaflet";
import "leaflet/dist/leaflet.css";
import { useNavigate } from "react-router-dom";

const CENTER = { lat: 14.1467, lng: 121.4708 };
const MAX_TOP_VISITED = 10;

export default function Home() {
  const navigate = useNavigate();
  const [spots, setSpots] = useState([]);
  const [route, setRoute] = useState([]);
  const [weather, setWeather] = useState({ weather: [{ main: "Clear" }] });
  const [userPreferences, setUserPreferences] = useState({
    preferNature: true,
    preferCulture: false,
  });
  const [startTime, setStartTime] = useState("");
  const mapRef = useRef(null);
  const markerRefs = useRef({});

  // Load POIs from API
  useEffect(() => {
    fetch("http://localhost:5000/api/pois")
      .then((res) => res.json())
      .then((data) => {
        const rows = data
          .filter((r) => r.name && r.lat && r.lng)
          .map((r) => ({
            name: r.name.trim(),
            lat: parseFloat(r.lat),
            lng: parseFloat(r.lng),
            visitors: r.visitors?.trim() || "Moderate",
            image: r.image || null,
            description: r.description || "No description",
            type: r.tags || "nature",
          }));
        setSpots(rows);
      })
      .catch((err) => console.error("Error fetching POIs:", err));
  }, []);

  const calculateDistance = (a, b) => {
    const R = 6371;
    const dLat = ((b.lat - a.lat) * Math.PI) / 180;
    const dLng = ((b.lng - a.lng) * Math.PI) / 180;
    const lat1 = (a.lat * Math.PI) / 180;
    const lat2 = (b.lat * Math.PI) / 180;
    const aa =
      Math.sin(dLat / 2) * Math.sin(dLat / 2) +
      Math.sin(dLng / 2) * Math.sin(dLng / 2) * Math.cos(lat1) * Math.cos(lat2);
    const c = 2 * Math.atan2(Math.sqrt(aa), Math.sqrt(1 - aa));
    return R * c;
  };

  const orderByDistance = (spots) => {
    if (!spots.length) return [];
    const ordered = [];
    const remaining = [...spots];
    let current = remaining.shift();
    ordered.push(current);
    while (remaining.length) {
      let nearestIndex = 0;
      let minDistance = calculateDistance(current, remaining[0]);
      for (let i = 1; i < remaining.length; i++) {
        const dist = calculateDistance(current, remaining[i]);
        if (dist < minDistance) {
          minDistance = dist;
          nearestIndex = i;
        }
      }
      current = remaining.splice(nearestIndex, 1)[0];
      ordered.push(current);
    }
    return ordered;
  };

  const scoreSpots = () => {
    return spots
      .map((spot) => {
        let score = 0;
        if (userPreferences.preferNature && spot.type.includes("nature"))
          score += 0.3;
        if (userPreferences.preferCulture && spot.type.includes("history"))
          score += 0.2;
        if (
          weather.weather[0].main.toLowerCase().includes("rain") &&
          spot.type.includes("nature")
        )
          score -= 0.2;
        const visitorRanking = { "Very High": 3, High: 2, Moderate: 1, Low: 0 };
        score += visitorRanking[spot.visitors] || 1;
        return { ...spot, score };
      })
      .sort((a, b) => b.score - a.score)
      .slice(0, MAX_TOP_VISITED);
  };

  const generateItinerary = () => {
    const start = startTime ? new Date(startTime) : new Date();
    const scored = scoreSpots();
    const optimized = orderByDistance(scored);

    const itinerary = optimized.map((spot, i) => {
      const arrival = new Date(start.getTime() + i * 2 * 60 * 60 * 1000);
      const departure = new Date(arrival.getTime() + 2 * 60 * 60 * 1000);
      return { ...spot, arrival, departure };
    });

    setRoute(itinerary);
    navigate("/route-details", { state: { itinerary } });
  };

  const topVisited = [...spots]
    .sort((a, b) => {
      const visitorRanking = { "Very High": 3, High: 2, Moderate: 1, Low: 0 };
      return (visitorRanking[b.visitors] || 1) - (visitorRanking[a.visitors] || 1);
    })
    .slice(0, MAX_TOP_VISITED);

  const focusOnSpot = (spot) => {
    if (mapRef.current) {
      mapRef.current.flyTo([spot.lat, spot.lng], 15, { duration: 1.5 });
    }
    if (markerRefs.current[spot.name]) {
      markerRefs.current[spot.name].openPopup();
    }
  };

  return (
    <div
      style={{
        display: "grid",
        gap: "20px",
        gridTemplateColumns: "1fr 1fr",
        padding: "16px",
      }}
    >
      <div
        className="card"
        style={{
          background: "#fff",
          borderRadius: "12px",
          padding: "16px",
          boxShadow: "0 4px 10px rgba(0,0,0,0.1)",
        }}
      >
        <h2
          style={{
            fontSize: "20px",
            fontWeight: "bold",
            marginBottom: "12px",
            color: "#333",
            borderBottom: "2px solid #eee",
            paddingBottom: "6px",
          }}
        >
          Majayjay Overview
        </h2>
        <div
          className="map-wrap"
          style={{ height: "500px", borderRadius: "10px", overflow: "hidden" }}
        >
          {spots.length > 0 ? (
            <MapContainer
              center={CENTER}
              zoom={13}
              style={{ height: "100%", width: "100%" }}
              whenCreated={(mapInstance) => {
                mapRef.current = mapInstance;
              }}
            >
              <TileLayer
                attribution='&copy; <a href="https://www.openstreetmap.org/">OSM contributors</a>'
                url="https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png"
              />
              {spots.map((s, i) => (
                <Marker
                  key={i}
                  position={[s.lat, s.lng]}
                  ref={(ref) => {
                    if (ref) markerRefs.current[s.name] = ref;
                  }}
                >
                  <Popup>
                    <strong style={{ color: "#4f46e5" }}>{s.name}</strong>
                    <br />
                    Visitors: {s.visitors}
                    {s.image && (
                      <>
                        <br />
                        <img
                          src={s.image}
                          alt={s.name}
                          style={{
                            width: "150px",
                            borderRadius: "8px",
                            marginTop: "6px",
                            boxShadow: "0 2px 6px rgba(0,0,0,0.15)",
                          }}
                        />
                      </>
                    )}
                    <p
                      style={{
                        marginTop: "6px",
                        fontSize: "14px",
                        color: "#555",
                      }}
                    >
                      {s.description}
                    </p>
                  </Popup>
                </Marker>
              ))}
            </MapContainer>
          ) : (
            <div
              style={{
                textAlign: "center",
                color: "#888",
                fontStyle: "italic",
                padding: "20px",
              }}
            >
              No spots available
            </div>
          )}
        </div>
      </div>

      <div
        className="card"
        style={{
          background: "#fff",
          borderRadius: "12px",
          padding: "16px",
          boxShadow: "0 4px 10px rgba(0,0,0,0.1)",
        }}
      >
        <h2
          style={{
            fontSize: "20px",
            fontWeight: "bold",
            marginBottom: "12px",
            color: "#333",
            borderBottom: "2px solid #eee",
            paddingBottom: "6px",
          }}
        >
          Top {MAX_TOP_VISITED} Most Visited Places
        </h2>

        <ul
          style={{
            margin: 0,
            paddingLeft: "20px",
            color: "#444",
            lineHeight: "1.6",
          }}
        >
          {topVisited.map((s, i) => (
            <li
              key={i}
              style={{ marginBottom: "8px", cursor: "pointer" }}
              onClick={() => focusOnSpot(s)}
            >
              <strong style={{ color: "#4f46e5" }}>{s.name}</strong> â€”{" "}
              {s.visitors} visitors
            </li>
          ))}
        </ul>

        <div style={{ marginTop: "18px" }}>
          <label
            style={{
              fontWeight: "500",
              color: "#555",
              marginBottom: "6px",
              display: "block",
            }}
          >
            Start Time:
          </label>
          <input
            type="datetime-local"
            value={startTime}
            onChange={(e) => setStartTime(e.target.value)}
            style={{
              width: "100%",
              padding: "8px",
              borderRadius: "8px",
              border: "1px solid #ccc",
              outline: "none",
            }}
          />
        </div>

        <button
          onClick={generateItinerary}
          style={{
            marginTop: "12px",
            padding: "10px 16px",
            background: "linear-gradient(90deg, #4f46e5, #6366f1)",
            color: "#fff",
            fontWeight: "600",
            borderRadius: "8px",
            border: "none",
            cursor: "pointer",
            transition: "0.2s ease",
          }}
          onMouseOver={(e) => (e.currentTarget.style.opacity = "0.9")}
          onMouseOut={(e) => (e.currentTarget.style.opacity = "1")}
        >
          Generate Route
        </button>
      </div>
    </div>
  );
}
