import React, { useEffect, useState } from "react";
import { MapContainer, TileLayer, Marker, Popup, Polyline } from "react-leaflet";
import "leaflet/dist/leaflet.css";
import { useNavigate, useLocation } from "react-router-dom";

export default function RouteDetails() {
  const location = useLocation();
  const navigate = useNavigate();
  const routeSpots = location.state?.itinerary || [];
  const [currentIndex, setCurrentIndex] = useState(0);
  const [fullRoute, setFullRoute] = useState([]);
  const [loadingRoute, setLoadingRoute] = useState(true);

  // Fetch ORS route between two POIs
  const getRouteFromORS = async (from, to) => {
    const profile = from.type?.toLowerCase() === "forest" ? "foot-hiking" : "driving-car";
    const apiKey = "YOUR_API_KEY"; // Replace with your ORS API key
    const url = `https://api.openrouteservice.org/v2/directions/${profile}?api_key=${apiKey}&start=${from.lng},${from.lat}&end=${to.lng},${to.lat}`;

    try {
      const res = await fetch(url);
      const data = await res.json();
      const coords = data.features[0].geometry.coordinates.map(([lng, lat]) => [lat, lng]);
      return coords;
    } catch (err) {
      console.error("ORS route error:", err);
      return [[from.lat, from.lng], [to.lat, to.lng]]; // fallback straight line
    }
  };

  // Build the full route
  useEffect(() => {
    const buildRoute = async () => {
      const segments = [];
      for (let i = 0; i < routeSpots.length - 1; i++) {
        const segment = await getRouteFromORS(routeSpots[i], routeSpots[i + 1]);
        segments.push(segment);
      }
      setFullRoute(segments.flat());
      setLoadingRoute(false);
    };
    if (routeSpots.length) buildRoute();
  }, [routeSpots]);

  const nextPOI = () => {
    if (currentIndex < routeSpots.length - 1) setCurrentIndex(currentIndex + 1);
    else alert("You have reached the end of the route!");
  };

  const finishRoute = () => {
    if (window.confirm("Are you sure you want to finish the route?")) {
      alert("Route completed!");
      navigate("/");
    }
  };

  const cancelRoute = () => {
    if (window.confirm("Are you sure you want to cancel the route? You will lose progress.")) {
      alert("Route cancelled.");
      navigate("/");
    }
  };

  const currentPOI = routeSpots[currentIndex];

  if (!routeSpots.length) return <p>No route available.</p>;

  return (
    <div style={{ display: "grid", gridTemplateColumns: "1fr 1fr", gap: 16 }}>
      <div style={{ height: "600px" }}>
        {loadingRoute ? (
          <p>Loading route...</p>
        ) : (
          <MapContainer center={[currentPOI.lat, currentPOI.lng]} zoom={14} style={{ height: "100%", width: "100%" }}>
            <TileLayer
              attribution='&copy; <a href="https://www.openstreetmap.org/">OSM contributors</a>'
              url="https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png"
            />
            <Polyline positions={fullRoute} color="blue" />
            {routeSpots.map((spot, i) => (
              <Marker key={i} position={[spot.lat, spot.lng]}>
                <Popup>
                  <strong>{spot.name}</strong>
                  <p>{spot.description}</p>
                  {spot.image && <img src={spot.image} alt={spot.name} style={{ width: "150px", borderRadius: "8px" }} />}
                </Popup>
              </Marker>
            ))}
          </MapContainer>
        )}
      </div>

      <div>
        <h2>Current POI:</h2>
        <p><strong>{currentPOI.name}</strong></p>
        <p>{currentPOI.description}</p>
        {currentPOI.image && <img src={currentPOI.image} alt={currentPOI.name} style={{ width: "200px", borderRadius: "8px" }} />}

        <p style={{ marginTop: 20 }}>POI {currentIndex + 1} of {routeSpots.length}</p>
      </div>

      {/* Sticky Button Bar */}
      <div style={{
        position: "fixed",
        bottom: 0,
        left: 0,
        right: 0,
        background: "#fff",
        padding: "10px",
        borderTop: "1px solid #ccc",
        display: "flex",
        justifyContent: "center",
        gap: "10px",
        zIndex: 1000
      }}>
        {currentIndex < routeSpots.length - 1 && <button onClick={nextPOI}>Next POI</button>}
        <button onClick={cancelRoute}>Cancel Route</button>
        {currentIndex === routeSpots.length - 1 && <button onClick={finishRoute}>Finish Route</button>}
      </div>
    </div>
  );
}
