export default function RoutePlanner(spots, weather, setRoute) {
  if (!spots.length) return alert("Select at least one spot!");

  // Dummy ML-like scoring (simplified for now)
  let scored = spots.map(s => {
    let score = Math.random();
    if (weather && weather.weather[0].main.toLowerCase().includes("rain") && s.type === "nature") {
      score -= 0.3; // penalize nature spots during rain
    }
    return { ...s, score };
  });

  scored.sort((a, b) => b.score - a.score);
  setRoute(scored);
}
