import React, { useState, useEffect } from "react";
import { useNavigate } from "react-router-dom";

export default function RoutePlanner() {
  const [pois, setPois] = useState([]);
  const [user, setUser] = useState(null);
  const navigate = useNavigate();

  useEffect(() => {
    const storedUser = JSON.parse(localStorage.getItem("user"));
    setUser(storedUser);

    fetch("http://localhost:5000/api/pois")
      .then(res => res.json())
      .then(data => setPois(data))
      .catch(() => setPois([]));
  }, []);

  const recommendRoute = () => {
    if (!user || !user.preferences) {
      alert("Set your preferences first in profile.");
      return;
    }

    const prefs = user.preferences.toLowerCase().split(",");
    const filtered = pois.filter(poi =>
      prefs.some(pref => poi.tags?.toLowerCase().includes(pref.trim()))
    );

    const selected = filtered.slice(0, 4);
    if (selected.length === 0) {
      alert("No POIs match your preferences.");
      return;
    }

    navigate("/route-details", { state: { itinerary: selected } });
  };

  return (
    <div style={{ textAlign: "center", marginTop: 40 }}>
      <button
        onClick={recommendRoute}
        style={{
          padding: "12px 24px",
          background: "#2196f3",
          color: "#fff",
          border: "none",
          borderRadius: 8,
          fontSize: 16,
          cursor: "pointer"
        }}
      >
        Plan My Route
      </button>
    </div>
  );
}
