import React, { useState, useEffect } from "react";
import { useNavigate } from "react-router-dom";

const ORS_API_KEY = "eyJvcmciOiI1YjNjZTM1OTc4NTExMTAwMDFjZjYyNDgiLCJpZCI6ImJhNWU3NmIxOTRmMzQyYTg4NzY4MDc2MzVmZDY1ZDRhIiwiaCI6Im11cm11cjY0In0=";

export default function RoutePlanner() {
  const [pois, setPois] = useState([]);
  const [user, setUser] = useState(null);
  const [showPopup, setShowPopup] = useState(false);
  const [startTime, setStartTime] = useState("now");
  const [scheduledDate, setScheduledDate] = useState("");
  const [mode, setMode] = useState("car");
  const [startChoice, setStartChoice] = useState("address"); // address or poi
  const [selectedStartPoi, setSelectedStartPoi] = useState("");
  const navigate = useNavigate();

  useEffect(() => {
    const storedUser = JSON.parse(localStorage.getItem("user"));
    setUser(storedUser);

    fetch("http://localhost:5000/api/pois")
      .then((res) => res.json())
      .then((data) => setPois(data))
      .catch((err) => {
        console.error("Error fetching POIs:", err);
        setPois([]);
      });
  }, []);

  // Request route info from ORS
  const getRoute = async (start, end, mode) => {
    const res = await fetch(
      `https://api.openrouteservice.org/v2/directions/${mode}`,
      {
        method: "POST",
        headers: {
          Authorization: ORS_API_KEY,
          "Content-Type": "application/json",
        },
        body: JSON.stringify({
          coordinates: [start, end], // [lng, lat]
        }),
      }
    );

    if (!res.ok) throw new Error("Routing failed");
    const data = await res.json();
    return {
      distance: data.routes[0].summary.distance, // meters
      geometry: data.routes[0].geometry, // polyline (encoded)
    };
  };

  const recommendRoute = async () => {
    if (!user || !user.preferences || user.preferences.length === 0) {
      alert("Set your preferences first in profile.");
      return;
    }

    // Determine start point
    let startCoords = null;
    if (startChoice === "address" && user.address_coords) {
      startCoords = user.address_coords; // [lng, lat]
    } else if (startChoice === "poi" && selectedStartPoi) {
      const poi = pois.find((p) => p.id === parseInt(selectedStartPoi));
      if (poi) startCoords = [parseFloat(poi.lng), parseFloat(poi.lat)];
    }

    if (!startCoords) {
      alert("Please choose a valid starting point.");
      return;
    }

    const prefs = user.preferences.map((p) => p.toLowerCase());

    // Score POIs
    const scored = await Promise.all(
      pois.map(async (poi) => {
        let score = 0;
        prefs.forEach((pref) => {
          if (poi.tags?.toLowerCase().includes(pref)) score += 5;
        });

        try {
          const { distance } = await getRoute(
            startCoords,
            [poi.lng, poi.lat],
            mode === "foot"
              ? "foot-walking"
              : mode === "bike"
              ? "cycling-regular"
              : "driving-car"
          );
          const km = distance / 1000;
          if (mode === "foot" || mode === "bike") {
            score += 10 / (km + 0.5);
          } else {
            score += 5 / (km + 1);
          }
          return { ...poi, score, distance: km };
        } catch {
          return { ...poi, score: 0, distance: Infinity };
        }
      })
    );

    // Sort & select
    const sorted = scored.sort((a, b) => b.score - a.score);
    const selected = sorted.slice(0, 5);

    if (selected.length === 0) {
      alert("No POIs match your preferences.");
      return;
    }

    // Build segments with mixed mode logic
    const segments = [];
    let current = startCoords;

    for (let poi of selected) {
      try {
        let travelMode = mode === "foot" ? "foot-walking" : "driving-car";
        let style = "solid";

        // If user didn’t pick walking, check distance
        if (mode !== "foot" && poi.distance < 0.5) {
          travelMode = "foot-walking";
          style = "dashed";
        } else if (mode === "bike") {
          travelMode = "cycling-regular";
        }

        const { distance, geometry } = await getRoute(
          current,
          [poi.lng, poi.lat],
          travelMode
        );

        segments.push({
          from: current,
          to: [poi.lng, poi.lat],
          poi,
          distance,
          geometry,
          travelMode,
          style,
        });

        current = [poi.lng, poi.lat];
      } catch (err) {
        console.error("Segment routing failed:", err);
      }
    }

    setShowPopup(false);

    navigate("/routedetails", {
      state: {
        itinerary: selected,
        startPoint: startCoords,
        segments,
        transportMode: mode,
        startTime: startTime === "now" ? new Date().toISOString() : scheduledDate,
      },
    });
  };

  return (
    <div style={{ textAlign: "center", marginTop: 40 }}>
      <button
        onClick={() => setShowPopup(true)}
        style={{
          padding: "12px 24px",
          background: "#2196f3",
          color: "#fff",
          border: "none",
          borderRadius: 8,
          fontSize: 16,
          cursor: "pointer",
        }}
      >
        Plan My Route
      </button>

      {showPopup && (
        <div
          style={{
            position: "fixed",
            top: 0,
            left: 0,
            width: "100%",
            height: "100%",
            background: "rgba(0,0,0,0.6)",
            display: "flex",
            alignItems: "center",
            justifyContent: "center",
            zIndex: 9999,
          }}
        >
          <div
            style={{
              background: "#fff",
              padding: 20,
              borderRadius: 10,
              width: 340,
              textAlign: "center",
              zIndex: 10000,
            }}
          >
            <h3 style={{ marginBottom: 12 }}>Plan Your Trip</h3>

            {/* Start Time */}
            <label>
              <input
                type="radio"
                value="now"
                checked={startTime === "now"}
                onChange={() => setStartTime("now")}
              />{" "}
              Start Now
            </label>
            <br />
            <label>
              <input
                type="radio"
                value="later"
                checked={startTime === "later"}
                onChange={() => setStartTime("later")}
              />{" "}
              Start Later
            </label>
            {startTime === "later" && (
              <input
                type="datetime-local"
                value={scheduledDate}
                onChange={(e) => setScheduledDate(e.target.value)}
                style={{ marginTop: 8, display: "block", width: "100%" }}
              />
            )}

            <hr style={{ margin: "12px 0" }} />

            {/* Transport Mode */}
            <label>Mode of Transport:</label>
            <select
              value={mode}
              onChange={(e) => setMode(e.target.value)}
              style={{ display: "block", margin: "8px auto", padding: 6 }}
            >
              <option value="foot">On Foot</option>
              <option value="bike">Bike</option>
              <option value="motor">Motor</option>
              <option value="car">Car</option>
            </select>

            <hr style={{ margin: "12px 0" }} />

            {/* Start Point Choice */}
            <label>Starting Point:</label>
            <div>
              <label>
                <input
                  type="radio"
                  value="address"
                  checked={startChoice === "address"}
                  onChange={() => setStartChoice("address")}
                />{" "}
                My Address
              </label>
              <br />
              <label>
                <input
                  type="radio"
                  value="poi"
                  checked={startChoice === "poi"}
                  onChange={() => setStartChoice("poi")}
                />{" "}
                Choose a POI
              </label>
            </div>

            {startChoice === "poi" && (
              <select
                value={selectedStartPoi}
                onChange={(e) => setSelectedStartPoi(e.target.value)}
                style={{
                  display: "block",
                  margin: "8px auto",
                  padding: 6,
                  width: "100%",
                }}
              >
                <option value="">Select a POI</option>
                {pois.map((poi) => (
                  <option key={poi.id} value={poi.id}>
                    {poi.name}
                  </option>
                ))}
              </select>
            )}

            <div style={{ marginTop: 16 }}>
              <button
                onClick={recommendRoute}
                style={{
                  marginRight: 8,
                  padding: "8px 16px",
                  background: "#3b82f6",
                  color: "#fff",
                  border: "none",
                  borderRadius: 6,
                  cursor: "pointer",
                }}
              >
                Confirm
              </button>
              <button
                onClick={() => setShowPopup(false)}
                style={{
                  padding: "8px 16px",
                  background: "#aaa",
                  color: "#fff",
                  border: "none",
                  borderRadius: 6,
                  cursor: "pointer",
                }}
              >
                Cancel
              </button>
            </div>
          </div>
        </div>
      )}
    </div>
  );
}
